// Function to calculate the total number of characters in a vector of strings
int total_chars(const vector<string>& vec) {
    int total = 0;
    for (const auto& str : vec) {
        total += str.length();
    }
    return total;
}

// Function to compare two vectors and return the one with fewer total characters
vector<string> total_match(vector<string> lst1, vector<string> lst2) {
    int total1 = total_chars(lst1);
    int total2 = total_chars(lst2);

    if (total1 <= total2) {
        return lst1;
    } else {
