using namespace std;

// Function to generate all prime numbers less than 100 using Sieve of Eratosthenes
vector<int> generate_primes() {
    vector<bool> is_prime(100, true);
    vector<int> primes;
    is_prime[0] = is_prime[1] = false; // 0 and 1 are not prime numbers

    for (int i = 2; i < 100; ++i) {
        if (is_prime[i]) {
            primes.push_back(i);
            for (int j = i * i; j < 100; j += i) {
                is_prime[j] = false;
            }
        }
    }
    return primes;
}

// Function to check if the given number is the product of three prime numbers
bool is_multiply_prime(int a) {
    vector<int> primes = generate_primes();
    int n = primes.size();

    for (int i = 0; i < n; ++i) {
        for (int j = i; j < n; ++j) {
            for (int k = j; k < n; ++k) {
                if (primes[i] * primes[j] * primes[k] == a) {
                    return true;
                }
            }
        }
    }
    return false;
}


#undef NDEBUG
#include<assert.h>
int main(){
    assert (is_multiply_prime(5) == false);
    assert (is_multiply_prime(30) == true);
    assert (is_multiply_prime(8) == true);
    assert (is_multiply_prime(10) == false);
    assert (is_multiply_prime(125) == true);
    assert (is_multiply_prime(3 * 5 * 7) == true);
    assert (is_multiply_prime(3 * 6 * 7) == false);
    assert (is_multiply_prime(9 * 9 * 9) == false);
    assert (is_multiply_prime(11 * 9 * 9) == false);
    assert (is_multiply_prime(11 * 13 * 7) == true);
}

