bool is_happy(const string& s) {
    // Check if the length of the string is less than 3
    if (s.length() < 3) {
        return false;
    }

    // Iterate through the string and check every 3 consecutive characters
    for (size_t i = 0; i < s.length() - 2; ++i) {
        if (s[i] == s[i + 1] || s[i] == s[i + 2] || s[i + 1] == s[i + 2]) {
            return false;
        }
    }

    // If all sets of 3 consecutive characters are distinct, the string is happy
    return true;
}


