bool is_simple_power(int x, int n) {
    // Handle edge cases
    if (n == 1) {
        return x == 1; // Only 1^k = 1 for any k
    }
    if (x == 1) {
        return n == 1; // Only 1^k = 1 for any k
    }
    if (n <= 0 || x <= 0) {
        return false; // Negative numbers or zero cannot be simple powers in this context
    }

    // Calculate the logarithm of x to the base n
    double k = log(x) / log(n);

    // Check if k is an integer
    return floor(k) == k;
}


#undef NDEBUG
#include<assert.h>
int main(){
    assert (is_simple_power(1, 4)== true);
    assert (is_simple_power(2, 2)==true);
    assert (is_simple_power(8, 2)==true);
    assert (is_simple_power(3, 2)==false);
