int starts_one_ends(int n) {
    if (n == 1) {
        return 1; // Only one 1-digit number that starts or ends with 1
    }

    int count_start_with_1 = pow(10, n - 1);
    int count_end_with_1 = 9 * pow(10, n - 2);
    int count_start_and_end_with_1 = pow(10, n - 2);

    return count_start_with_1 + count_end_with_1 - count_start_and_end_with_1;
}


