// Function to calculate the sum of digits of a number
int sumOfDigits(int N) {
    int sum = 0;
    while (N > 0) {
        sum += N % 10;
        N /= 10;
    }
    return sum;
}

// Function to convert a number to its binary representation as a string
string toBinary(int num) {
    if (num == 0) return "0";
    string binary = "";
    while (num > 0) {
        binary = to_string(num % 2) + binary;
        num /= 2;
    }
    return binary;
}

// Main function to solve the problem
string solve(int N) {
    int sum = sumOfDigits(N);
    return toBinary(sum);
}

// Function to test the solution
void test() {
    cout << solve(1000) << " (Expected: 1)" << endl;
    cout << solve(150) << " (Expected: 110)" << endl;
    cout << solve(147) << " (Expected: 1100)" << endl;
}


