string anti_shuffle(string s) {
    int n = s.length();
    string result = "";
    string word = "";

    for (int i = 0; i <= n; ++i) {
        if (i < n && s[i] != ' ') {
            // Accumulate characters of the current word
            word += s[i];
        } else {
            // Sort the characters of the current word
            sort(word.begin(), word.end());
            // Append the sorted word to the result
            result += word;
            // If not at the end of the string, add a space
            if (i < n) {
                result += ' ';
            }
            // Reset the word for the next iteration
            word = "";
        }
    }

    return result;
}

// Function to test the anti_shuffle function
void test_anti_shuffle() {
    cout << anti_shuffle("Hi") << endl;  // Output: "Hi"
    cout << anti_shuffle("hello") << endl;  // Output: "ehllo"
    cout << anti_shuffle("Hello World!!!") << endl;  // Output: "Hello !!!Wdlor"
}


