// Function to compare two coordinates for sorting
bool compare(const vector<int>& a, const vector<int>& b) {
    if (a[0] == b[0]) {
        // If the row is the same, sort by column in descending order
        return a[1] > b[1];
    }
    // Otherwise, sort by row in ascending order
    return a[0] < b[0];
}

vector<vector<int>> get_row(vector<vector<int>> lst, int x) {
    vector<vector<int>> result;

    // Iterate through each row
    for (int i = 0; i < lst.size(); ++i) {
        // Iterate through each element in the current row
        for (int j = 0; j < lst[i].size(); ++j) {
            // If the element matches x, add its coordinates to the result
            if (lst[i][j] == x) {
                result.push_back({i, j});
            }
        }
    }

    // Sort the result based on the custom comparator
    sort(result.begin(), result.end(), compare);

    return result;
}

