vector<int> sort_array(vector<int> array) {
    // Create a copy of the original vector to avoid modifying it
    vector<int> sorted_array = array;

    // Check if the vector is empty or has only one element
    if (array.empty() || array.size() == 1) {
        return sorted_array;
    }

    // Calculate the sum of the first and last elements
    int sum = array.front() + array.back();

    // Determine the sorting order based on the sum
    if (sum % 2 == 0) {
        // Sort in descending order if the sum is even
        sort(sorted_array.begin(), sorted_array.end(), greater<int>());
    } else {
        // Sort in ascending order if the sum is odd
        sort(sorted_array.begin(), sorted_array.end());
    }

    return sorted_array;
}

