optional<int> next_smallest(const vector<int>& lst) {
    if (lst.size() < 2) {
        return nullopt; // Not enough elements to find the second smallest
    }

    int first = INT_MAX, second = INT_MAX;
    for (int num : lst) {
        if (num < first) {
            second = first;
            first = num;
        } else if (num < second && num != first) {
            second = num;
        }
    }

    if (second == INT_MAX) {
        return nullopt; // No second smallest element found
    }

    return second;
}

