// Function to encrypt the string by rotating the alphabet
string encrypt(string s) {
    for (char &c : s) {
        if (isalpha(c)) {
            char base = islower(c) ? 'a' : 'A';
            c = base + (c - base + 4) % 26;
        }
    }
    return s;
}


