int is_bored(const string& S) {
    // Vector to store the sentences
    vector<string> sentences;
    // String stream to help split the input string
    stringstream ss(S);
    string token;

    // Split the string into sentences based on delimiters
    while (getline(ss, token, '.')) {
        sentences.push_back(token);
    }
    while (getline(ss, token, '?')) {
        sentences.push_back(token);
    }
    while (getline(ss, token, '!')) {
        sentences.push_back(token);
    }

    int boredom_count = 0;

    // Check each sentence to see if it starts with "I"
    for (const auto& sentence : sentences) {
        // Trim leading spaces
        size_t start = sentence.find_first_not_of(" ");
        if (start == string::npos) continue; // Skip empty sentences

        // Extract the first word
        string first_word = sentence.substr(start, 2); // "I" is 2 characters long

        // Check if the first word is "I"
        if (first_word == "I ") {
            boredom_count++;
        }
    }

    return boredom_count;
}


