// Helper function to check if a number is prime
bool isPrime(int n) {
    if (n <= 1) return false;
    if (n <= 3) return true;
    if (n % 2 == 0 || n % 3 == 0) return false;
    for (int i = 5; i * i <= n; i += 6) {
        if (n % i == 0 || n % (i + 2) == 0) return false;
    }
    return true;
}

// Function to find the largest prime and return the sum of its digits
int skjkasdkd(vector<int> lst) {
    int largestPrime = -1;

    // Find the largest prime in the list
    for (int num : lst) {
        if (isPrime(num) && num > largestPrime) {
            largestPrime = num;
        }
    }

    // If no prime number is found, return 0
    if (largestPrime == -1) return 0;

    // Calculate the sum of the digits of the largest prime
    int sumOfDigits = 0;
    while (largestPrime > 0) {
        sumOfDigits += largestPrime % 10;
        largestPrime /= 10;
    }

    return sumOfDigits;
}

