// Function to swap the case of a character
char swapCase(char c) {
    if (isupper(c)) return tolower(c);
    else return toupper(c);
}

// Function to replace vowels with the letter 2 places ahead in the alphabet
char replaceVowel(char c) {
    string vowels = "aeiouAEIOU";
    string replacements = "cgikmCGIKM";

    size_t pos = vowels.find(c);
    if (pos != string::npos) {
        return replacements[pos];
    }
    return c;
}

// Main encoding function
string encode(string message) {
    string encodedMessage = "";
    for (char c : message) {
        // Swap case
        char swapped = swapCase(c);
        // Replace vowel if necessary
        char replaced = replaceVowel(swapped);
        encodedMessage += replaced;
    }
    return encodedMessage;
}

// Function to test the encode function
void testEncode() {
    cout << encode("test") << endl; // Expected: TGST
    cout << encode("This is a message") << endl; // Expected: tHKS KS C MGSSCGG
}


