bool isLowercase(const string& str) {
    for (char ch : str) {
        if (!islower(ch)) return false;
    }
    return true;
}

bool isUppercase(const string& str) {
    for (char ch : str) {
        if (!isupper(ch)) return false;
    }
    return true;
}

bool check_dict_case(map<string, string> dict) {
    if (dict.empty()) return false;

    bool hasLower = false;
    bool hasUpper = false;

    for (const auto& pair : dict) {
        if (isLowercase(pair.first)) {
            hasLower = true;
        } else if (isUppercase(pair.first)) {
            hasUpper = true;
        } else {
            return false; // Mixed case or non-alphabetic characters
        }

        if (hasLower && hasUpper) return false; // Both lowercase and uppercase found
    }

    return true;
}


