int closest_integer(const string& value) {
    // Convert the string to a double
    stringstream ss(value);
    double num;
    ss >> num;

    // Determine the closest integer
    double fractionalPart = num - floor(num);
    if (fractionalPart == 0.5 || fractionalPart == -0.5) {
        // Rounding away from zero
        return num > 0 ? ceil(num) : floor(num);
    } else {
        // Standard rounding
        return round(num);
    }
}

