int choose_num(int x, int y) {
    // Ensure x is less than or equal to y
    if (x > y) {
        return -1;
    }

    // Start from y and decrement until we find an even number or reach x
    for (int i = y; i >= x; --i) {
        if (i % 2 == 0) {
            return i;
        }
    }

    // If no even number is found, return -1
    return -1;
}


