using namespace std;

string rounded_avg(int n, int m) {
    // Step 1: Check if n is greater than m
    if (n > m) {
        return "-1";
    }

    // Step 2: Calculate the sum of all integers from n to m
    long long sum = ((long long)m * (m + 1) / 2) - ((long long)(n - 1) * n / 2);

    // Step 3: Compute the average
    int count = m - n + 1;
    double average = (double)sum / count;

    // Step 4: Round the average to the nearest integer (rounding down if there's a tie)
    int rounded_average = round(average);

    // Step 5: Convert the rounded average to binary
    string binary = "";
    while (rounded_average > 0) {
        binary = to_string(rounded_average % 2) + binary;
        rounded_average /= 2;
    }

    return binary.empty() ? "0" : binary;
}


