// Function to check if a number contains any even digit
bool has_even_digit(int num) {
    while (num > 0) {
        int digit = num % 10;
        if (digit % 2 == 0) {
            return true;
        }
        num /= 10;
    }
    return false;
}

// Function to filter and sort the vector
vector<int> unique_digits(vector<int> x) {
    vector<int> result;

    // Filter out numbers with even digits
    for (int num : x) {
        if (!has_even_digit(num)) {
            result.push_back(num);
        }
    }

    // Sort the result vector
    sort(result.begin(), result.end());

    return result;
}

// Function to test the unique_digits function
void test_unique_digits() {
    vector<int> test1 = {15, 33, 1422, 1};
    vector<int> expected1 = {1, 15, 33};
    assert(unique_digits(test1) == expected1);

    vector<int> test2 = {152, 323, 1422, 10};
    vector<int> expected2 = {};
    assert(unique_digits(test2) == expected2);

    cout << "All tests passed!" << endl;
}


