vector<string> by_length(vector<int> arr) {
    // Define the mapping from numbers to words
    const vector<string> numberWords = {"One", "Two", "Three", "Four", "Five", "Six", "Seven", "Eight", "Nine"};

    // Filter, sort, and reverse the array
    vector<int> filteredArr;
    for (int num : arr) {
        if (num >= 1 && num <= 9) {
            filteredArr.push_back(num);
        }
    }

    sort(filteredArr.begin(), filteredArr.end());
    reverse(filteredArr.begin(), filteredArr.end());

    // Convert numbers to words
    vector<string> result;
    for (int num : filteredArr) {
        result.push_back(numberWords[num - 1]);
    }

    return result;
}

