// Function to calculate the factorial of a number
int factorial(int num) {
    int result = 1;
    for (int i = 1; i <= num; ++i) {
        result *= i;
    }
    return result;
}

// Function to calculate the sum of numbers from 1 to num
int sumTo(int num) {
    return (num * (num + 1)) / 2;
}

// Main function to generate the vector
vector<int> f(int n) {
    vector<int> result(n);
    for (int i = 1; i <= n; ++i) {
        if (i % 2 == 0) {
            // If i is even, store the factorial of i
            result[i - 1] = factorial(i);
        } else {
            // If i is odd, store the sum of numbers from 1 to i
            result[i - 1] = sumTo(i);
        }
    }
    return result;
}

// Function to test the implementation
void test() {
    vector<int> result = f(5);
    for (int num : result) {
        std::cout << num << " ";
    }
    std::cout << std::endl;
}


