using namespace std;

bool move_one_ball(vector<int> arr) {
    if (arr.empty()) {
        return true;
    }

    int n = arr.size();
    int breakPoint = -1;

    // Find the break point
    for (int i = 0; i < n - 1; ++i) {
        if (arr[i] > arr[i + 1]) {
            if (breakPoint != -1) {
                // More than one break point found
                return false;
            }
            breakPoint = i;
        }
    }

    // Check if the array is already sorted
    if (breakPoint == -1) {
        return true;
    }

    // Check if the array can be sorted by a single right shift
    if (arr[n - 1] > arr[0]) {
        return false;
    }

    // Verify the rest of the array
    for (int i = 0; i < breakPoint; ++i) {
        if (arr[i] > arr[i + 1]) {
            return false;
        }
    }

    return true;
}

#undef NDEBUG
#include<assert.h>
int main(){
    assert (move_one_ball({3, 4, 5, 1, 2})==true);
    assert (move_one_ball({3, 5, 10, 1, 2})==true);
    assert (move_one_ball({4, 3, 1, 2})==false);
    assert (move_one_ball({3, 5, 4, 1, 2})==false);
    assert (move_one_ball({})==true);
}

