// Function to calculate the sum of digits of a number
int sum_of_digits(int num) {
    int sum = 0;
    bool is_negative = num < 0;
    if (is_negative) {
        num = -num; // Make the number positive for easier digit extraction
    }
    while (num > 0) {
        sum += num % 10;
        num /= 10;
    }
    if (is_negative) {
        sum -= 2 * (sum % 10); // Adjust the sum to account for the first digit being negative
    }
    return sum;
}

// Function to count the number of elements with a sum of digits > 0
int count_nums(vector<int> n) {
    int count = 0;
    for (int num : n) {
        if (sum_of_digits(num) > 0) {
            count++;
        }
    }
    return count;
}

// Example usage

#undef NDEBUG
#include<assert.h>
int main(){
    assert (count_nums({}) == 0);
    assert (count_nums({-1, -2, 0}) == 0);
    assert (count_nums({1, 1, 2, -2, 3, 4, 5}) == 6);
    assert (count_nums({1, 6, 9, -6, 0, 1, 5}) == 5);
    assert (count_nums({1, 100, 98, -7, 1, -1}) == 4);
    assert (count_nums({12, 23, 34, -45, -56, 0}) == 5);
    assert (count_nums({-0, 1}) == 1);
    assert (count_nums({1}) == 1);
}

