using namespace std;

string exchange(vector<int> lst1, vector<int> lst2) {
    int oddCount = 0; // Count of odd elements in lst1
    int evenCount = 0; // Count of even elements in lst2

    // Count odd elements in lst1
    for (int num : lst1) {
        if (num % 2 != 0) {
            oddCount++;
        }
    }

    // Count even elements in lst2
    for (int num : lst2) {
        if (num % 2 == 0) {
            evenCount++;
        }
    }

    // Check if there are enough even elements in lst2 to replace all odd elements in lst1
    if (evenCount >= oddCount) {
        return "YES";
    } else {
        return "NO";
    }
}

#undef NDEBUG
#include<assert.h>
int main(){
    assert (exchange({1, 2, 3, 4}, {1, 2, 3, 4}) == "YES");
    assert (exchange({1, 2, 3, 4}, {1, 5, 3, 4}) == "NO");
    assert (exchange({1, 2, 3, 4}, {2, 1, 4, 3}) == "YES" );
    assert (exchange({5, 7, 3}, {2, 6, 4}) == "YES");
    assert (exchange({5, 7, 3}, {2, 6, 3}) == "NO" );
    assert (exchange({3, 2, 6, 1, 8, 9}, {3, 5, 5, 1, 1, 1}) == "NO");
    assert (exchange({100, 200}, {200, 200}) == "YES");
}

