using namespace std;

map<char, int> histogram(const string& test) {
    map<char, int> freqMap;
    stringstream ss(test);
    char ch;

    // Count the frequency of each character
    while (ss >> ch) {
        freqMap[ch]++;
    }

    // Find the maximum frequency
    int maxFreq = 0;
    for (const auto& pair : freqMap) {
        if (pair.second > maxFreq) {
            maxFreq = pair.second;
        }
    }

    // Create a result map with only the characters that have the maximum frequency
    map<char, int> result;
    for (const auto& pair : freqMap) {
        if (pair.second == maxFreq) {
            result.insert(pair);
        }
    }

    return result;
}

// Function to test the histogram function
void testHistogram() {
    auto printMap = [](const map<char, int>& m) {
        cout << "{";
        for (const auto& pair : m) {
            cout << "{" << pair.first << ", " << pair.second << "} ";
        }
        cout << "}" << endl;
    };

    printMap(histogram("a b c")); // {{a, 1} {b, 1} {c, 1}}
    printMap(histogram("a b b a")); // {{a, 2} {b, 2}}
    printMap(histogram("a b c a b")); // {{a, 2} {b, 2}}
    printMap(histogram("b b b b a")); // {{b, 4}}
    printMap(histogram("")); // {}
}


