// Function to remove characters from s that are present in c and check if the result is a palindrome
vector<string> reverse_delete(string s, string c) {
    // Remove characters from s that are present in c
    s.erase(remove_if(s.begin(), s.end(), [&c](char ch) {
        return c.find(ch) != string::npos;
    }), s.end());

    // Check if the resulting string is a palindrome
    bool isPal = isPalindrome(s);

    // Prepare the result vector
    vector<string> result;
    result.push_back(s);
    result.push_back(isPal ? "True" : "False");

    return result;
}

