    cout << "(" << result1[0] << ",\"" << result1[1] << "\")" << endl; // Expected: (bcd,"False")

    vector<string> result2 = reverse_delete("abcdef", "b");
    cout << "(" << result2[0] << ",\"" << result2[1] << "\")" << endl; // Expected: (acdef,"False")

    vector<string> result3 = reverse_delete("abcdedcba", "ab");
