long long minSubArraySum(vector<long long> nums) {
    long long min_sum = LLONG_MAX; // Initialize min_sum to the largest possible value
    long long current_sum = 0;      // Initialize current_sum to 0

    for (long long num : nums) {
        current_sum += num; // Add the current number to the current_sum
        if (current_sum < min_sum) {
            min_sum = current_sum; // Update min_sum if current_sum is smaller
        }
        if (current_sum > 0) {
            current_sum = 0; // Reset current_sum to 0 if it becomes positive
        }
    }

    return min_sum;
}

