// Function to check if a character is a consonant
bool is_consonant(char c) {
    c = tolower(c);
    return isalpha(c) && c != 'a' && c != 'e' && c != 'i' && c != 'o' && c != 'u';
}

// Function to split the string into words
vector<string> split_string(const string& s) {
    vector<string> words;
    string word;
    for (char c : s) {
        if (c == ' ') {
            if (!word.empty()) {
                words.push_back(word);
                word.clear();
            }
        } else {
            word += c;
        }
    }
    if (!word.empty()) {
        words.push_back(word);
    }
    return words;
}

// Function to count consonants in a word
int count_consonants(const string& word) {
    int count = 0;
    for (char c : word) {
        if (is_consonant(c)) {
            count++;
        }
    }
    return count;
}

// Main function to select words with exactly n consonants
vector<string> select_words(string s, int n) {
    vector<string> words = split_string(s);
    vector<string> result;
    for (const string& word : words) {
        if (count_consonants(word) == n) {
            result.push_back(word);
        }
    }
    return result;
}

// Function to test the select_words function
void test_select_words() {
    vector<string> result;

    result = select_words("Mary had a little lamb", 4);
    assert(result == vector<string>{"little"});

    result = select_words("Mary had a little lamb", 3);
    assert(result == vector<string>{"Mary", "lamb"});

    result = select_words("simple white space", 2);
    assert(result == vector<string>{});

    result = select_words("Hello world", 4);
    assert(result == vector<string>{"world"});

    result = select_words("Uncle sam", 3);
    assert(result == vector<string>{"Uncle"});

    cout << "All tests passed!" << endl;
}


