using namespace std;

int max_fill(vector<vector<int>> grid, int capacity) {
    int total_lowers = 0;

    // Iterate through each well (row in the grid)
    for (const auto& well : grid) {
        int water_units = 0;

        // Calculate the total water units in the current well
        for (int unit : well) {
            water_units += unit;
        }

        // Calculate the number of times the bucket needs to be lowered for this well
        int lowers = ceil(static_cast<double>(water_units) / capacity);

        // Add the number of lowers for this well to the total
        total_lowers += lowers;
    }

    return total_lowers;
}

// Example usage

