// Function to count the number of ones in the binary representation of an integer
int count_ones(int n) {
    return bitset<32>(n).count();
}

// Custom comparator for sorting
bool custom_compare(int a, int b) {
    int count_a = count_ones(a);
    int count_b = count_ones(b);

    // If the number of ones is the same, compare the decimal values
    if (count_a == count_b) {
        return a < b;
    }
    // Otherwise, compare the number of ones
    return count_a < count_b;
}

vector<int> sort_array(vector<int> arr) {
    // Sort the array using the custom comparator
    sort(arr.begin(), arr.end(), custom_compare);
    return arr;
}

