    vector<int> expected2 = {-4, -2, -6, -5, -3};
    assert(sort_array(test2) == expected2);

    vector<int> test3 = {1, 0, 2, 3, 4};
    vector<int> expected3 = {0, 1, 2, 4, 3};
    assert(sort_array(test3) == expected3);

    cout << "All tests passed!" << endl;
}


