// Function to check if a character is a vowel
bool is_vowel(char c) {
    return c == 'A' || c == 'E' || c == 'I' || c == 'O' || c == 'U' ||
           c == 'a' || c == 'e' || c == 'i' || c == 'o' || c == 'u';
}

// Function to check if a character is a consonant
bool is_consonant(char c) {
    return !is_vowel(c) && isalpha(c);
}

// Function to find the closest vowel between two consonants from the right side
string get_closest_vowel(const string& word) {
    int n = word.length();

    // Iterate from the right side of the word
    for (int i = n - 2; i > 0; --i) {
        if (is_vowel(word[i]) && is_consonant(word[i - 1]) && is_consonant(word[i + 1])) {
            return string(1, word[i]);
        }
    }

    // If no such vowel is found, return an empty string
    return "";
}


