// Function to check if a string is balanced
bool is_balanced(const string& s) {
    int balance = 0;
    for (char c : s) {
        if (c == '(') {
            balance++;
        } else if (c == ')') {
            balance--;
        }
        if (balance < 0) {
            return false; // Early exit if imbalance is detected
        }
    }
    return balance == 0;
}

// Function to check if concatenating the two strings in some order results in a balanced string
string match_parens(vector<string> lst) {
    string str1 = lst[0];
    string str2 = lst[1];

    // Check both possible concatenations
    if (is_balanced(str1 + str2) || is_balanced(str2 + str1)) {
        return "Yes";
    } else {
        return "No";
    }
}

// Main function to test the solution

