int solution(vector<int> lst) {
    int sum = 0;
    // Iterate through the vector, considering only even indices
    for (size_t i = 0; i < lst.size(); i += 2) {
        // Check if the element at the current even index is odd
        if (lst[i] % 2 != 0) {
            sum += lst[i];
        }
    }
    return sum;
}

