vector<string> split_words(const string& txt) {
    // Check for whitespace
    if (txt.find(' ') != string::npos) {
        stringstream ss(txt);
        vector<string> words;
        string word;
        while (ss >> word) {
            words.push_back(word);
        }
        return words;
    }

    // Check for commas
    if (txt.find(',') != string::npos) {
        stringstream ss(txt);
        vector<string> words;
        string word;
        while (getline(ss, word, ',')) {
            words.push_back(word);
        }
        return words;
    }

    // Count lowercase letters with odd order
    int count = 0;
    for (char c : txt) {
        if (islower(c) && ((c - 'a') % 2 == 1)) {
            count++;
        }
    }

    return {to_string(count)};
}

// Function to test the solution
void test_split_words() {
    vector<string> result1 = split_words("Hello world!");
    assert(result1 == vector<string>{"Hello", "world!"});

    vector<string> result2 = split_words("Hello,world!");
    assert(result2 == vector<string>{"Hello", "world!"});

    vector<string> result3 = split_words("abcdef");
    assert(result3 == vector<string>{"3"});

    cout << "All tests passed!" << endl;
}


