    vector<int> result = get_odd_collatz(5);
    for (int num : result) {
        cout << num << " ";
    }
    cout << endl; // Expected output: 1 5

    result = get_odd_collatz(7);
    for (int num : result) {
        cout << num << " ";
    }
    cout << endl; // Expected output: 1 7 11 17 21 85
}


