using namespace std;

bool is_sorted(vector<int> lst) {
    if (lst.empty()) return true; // An empty list is considered sorted

    int prev = lst[0];
    int count = 1; // Count of the current number

    for (size_t i = 1; i < lst.size(); ++i) {
        if (lst[i] < prev) {
            // If the current number is less than the previous one, it's not sorted
            return false;
        } else if (lst[i] == prev) {
            // If the current number is the same as the previous one, increment the count
            count++;
            if (count > 2) {
                // If the count exceeds 2, return false
                return false;
            }
        } else {
            // Reset the count for a new number
            prev = lst[i];
            count = 1;
        }
    }

    return true;
}

#undef NDEBUG
#include<assert.h>
int main(){
    assert (is_sorted({5}) == true);
    assert (is_sorted({1, 2, 3, 4, 5}) == true);
    assert (is_sorted({1, 3, 2, 4, 5}) == false);
    assert (is_sorted({1, 2, 3, 4, 5, 6}) == true);
    assert (is_sorted({1, 2, 3, 4, 5, 6, 7}) == true);
    assert (is_sorted({1, 3, 2, 4, 5, 6, 7}) == false);
    assert (is_sorted({}) == true);
    assert (is_sorted({1}) == true);
    assert (is_sorted({3, 2, 1}) == false);
    assert (is_sorted({1, 2, 2, 2, 3, 4}) == false);
    assert (is_sorted({1, 2, 3, 3, 3, 4}) == false);
    assert (is_sorted({1, 2, 2, 3, 3, 4}) == true);
    assert (is_sorted({1, 2, 3, 4}) == true);
}

