using namespace std;

int prod_signs(vector<int> arr) {
    if (arr.empty()) {
        return -32768;
    }

    int sum_of_magnitudes = 0;
    int product_of_signs = 1;

    for (int num : arr) {
        if (num == 0) {
            product_of_signs = 0;
            break;
        }
        sum_of_magnitudes += abs(num);
        product_of_signs *= (num > 0) ? 1 : -1;
    }

    return sum_of_magnitudes * product_of_signs;
}

// Example usage

#undef NDEBUG
#include<assert.h>
int main(){
    assert (prod_signs({1, 2, 2, -4}) == -9);
    assert (prod_signs({0, 1}) == 0);
    assert (prod_signs({1, 1, 1, 2, 3, -1, 1}) == -10);
    assert (prod_signs({}) == -32768);
    assert (prod_signs({2, 4,1, 2, -1, -1, 9}) == 20);
    assert (prod_signs({-1, 1, -1, 1}) == 4);
    assert (prod_signs({-1, 1, 1, 1}) == -4);
    assert (prod_signs({-1, 1, 1, 0}) == 0);
}

