// Define a tuple to store the current path, its last position, and the current length
using State = tuple<vector<int>, int, int>;

// Comparator for the priority queue to ensure it behaves as a min-heap
struct Compare {
    bool operator()(const State& a, const State& b) {
        return lexicographical_compare(begin(get<0>(a)), end(get<0>(a)),
                                      begin(get<0>(b)), end(get<0>(b)));
    }
};

vector<int> minPath(vector<vector<int>>& grid, int k) {
    int n = grid.size();
    vector<vector<bool>> visited(n, vector<bool>(n, false));

    // Priority queue to store the states
    priority_queue<State, vector<State>, Compare> pq;

    // Initialize the priority queue with all possible starting points
    for (int i = 0; i < n; ++i) {
        for (int j = 0; j < n; ++j) {
            vector<int> path = {grid[i][j]};
            pq.push({path, i, j});
        }
    }

    // Directions for moving to neighboring cells
    vector<pair<int, int>> directions = {{0, 1}, {1, 0}, {0, -1}, {-1, 0}};

    while (!pq.empty()) {
        auto [path, x, y] = pq.top();
        pq.pop();

        // If the path length is k, return the path
        if (path.size() == k) {
            return path;
        }

        // Mark the current cell as visited
        visited[x][y] = true;

        // Explore all neighboring cells
        for (auto& [dx, dy] : directions) {
            int nx = x + dx, ny = y + dy;

            // Check if the new position is within bounds and not visited
            if (nx >= 0 && nx < n && ny >= 0 && ny < n && !visited[nx][ny]) {
                vector<int> new_path = path;
                new_path.push_back(grid[nx][ny]);
                pq.push({new_path, nx, ny});
            }
        }
    }

    return {}; // Should never reach here if input is valid
}


