int digits(int n) {
    int product = 1;
    bool hasOdd = false; // Flag to check if there is at least one odd digit

    while (n > 0) {
        int digit = n % 10; // Extract the last digit
        if (digit % 2 != 0) { // Check if the digit is odd
            product *= digit;
            hasOdd = true;
        }
        n /= 10; // Remove the last digit
    }

    return hasOdd ? product : 0;
}


