    result[1] = 3;

    // Function to compute tri(n)
    function<int(int)> computeTri = [&](int k) -> int {
        if (k == 0) return 1; // tri(0) is not defined in the problem, but we need it for the sequence
        if (k == 1) return 3;
        if (result[k] != 0) return result[k]; // Return cached result

        if (k % 2 == 0) {
            result[k] = 1 + k / 2;
        } else {
            result[k] = computeTri(k - 1) + computeTri(k - 2) + computeTri(k + 1);
        }
        return result[k];
    };

    // Compute all values from 0 to n
    for (int i = 2; i <= n; ++i) {
        computeTri(i);
    }

    return result;
}


