using namespace std;

bool is_nested(const string& str) {
    stack<char> brackets;
    bool nested = false;

    for (char ch : str) {
        if (ch == '[') {
            brackets.push(ch);
        } else if (ch == ']') {
            if (!brackets.empty()) {
                brackets.pop();
                // Check if the current closing bracket is nested
                if (!brackets.empty()) {
                    nested = true;
                }
            } else {
                // Unmatched closing bracket
                return false;
            }
        }
    }

    // The string is valid if the stack is empty and we found a nested bracket
    return nested && brackets.empty();
}


