bool check_if_last_char_is_a_letter(const string& txt) {
    // Check if the string is empty
    if (txt.empty()) {
        return false;
    }

    // Get the last character of the string
    char last_char = txt[txt.size() - 1];

    // Check if the last character is an alphabetical character
    if (isalpha(last_char)) {
        // Check if the last character is not part of a word
        // This means it should either be the only character in the string
        // or the character before it should be a space
        if (txt.size() == 1 || txt[txt.size() - 2] == ' ') {
            return true;
        }
    }

    return false;
}


