vector<int> largest_smallest_integers(vector<int> lst) {
    int largest_negative = numeric_limits<int>::min();
    int smallest_positive = numeric_limits<int>::max();

    for (int num : lst) {
        if (num < 0) {
            largest_negative = max(largest_negative, num);
        } else if (num > 0) {
            smallest_positive = min(smallest_positive, num);
        }
    }

    // If no negative or positive integers were found, set them to 0
    if (largest_negative == numeric_limits<int>::min()) {
        largest_negative = 0;
    }
    if (smallest_positive == numeric_limits<int>::max()) {
        smallest_positive = 0;
    }

    return {largest_negative, smallest_positive};
}

