int can_arrange(vector<int> arr) {
    // Start from the second last element and move backwards
    for (int i = arr.size() - 1; i > 0; --i) {
        if (arr[i] < arr[i - 1]) {
            return i;
        }
    }
    // If no such element is found, return -1
    return -1;
}

