// Helper function to compute the factorial of a single number
long long factorial(int num) {
    if (num == 0 || num == 1) {
        return 1;
    }
    long long result = 1;
    for (int i = 2; i <= num; ++i) {
        result *= i;
    }
    return result;
}

// Function to compute the Brazilian factorial
long long special_factorial(int n) {
    long long result = 1;
    for (int i = 1; i <= n; ++i) {
        result *= factorial(i);
    }
    return result;
}

