// Function to convert a string to a float, handling both '.' and ','
float stringToFloat(const std::string& str) {
    std::string temp = str;
    std::replace(temp.begin(), temp.end(), ',', '.');
    return std::stof(temp);
}

// Function to compare two values of any type
boost::any compare_one(boost::any a, boost::any b) {
    // Extract the values from the boost::any objects
    float valA, valB;

    if (a.type() == typeid(std::string)) {
        valA = stringToFloat(boost::any_cast<std::string>(a));
    } else if (a.type() == typeid(int)) {
        valA = static_cast<float>(boost::any_cast<int>(a));
    } else if (a.type() == typeid(float)) {
        valA = boost::any_cast<float>(a);
    } else {
        throw std::runtime_error("Unsupported type for first argument");
    }

    if (b.type() == typeid(std::string)) {
        valB = stringToFloat(boost::any_cast<std::string>(b));
    } else if (b.type() == typeid(int)) {
        valB = static_cast<float>(boost::any_cast<int>(b));
    } else if (b.type() == typeid(float)) {
        valB = boost::any_cast<float>(b);
    } else {
        throw std::runtime_error("Unsupported type for second argument");
    }

    // Compare the values
    if (valA == valB) {
        return std::string("None");
    } else if (valA > valB) {
        return a;
    } else {
        return b;
    }
}


#undef NDEBUG
#include<assert.h>
int main(){
    assert (boost::any_cast<int>(compare_one(1, 2)) == 2);
    assert (boost::any_cast<double>(compare_one(1, 2.5))== 2.5);
    assert (boost::any_cast<int>(compare_one(2, 3)) == 3);
    assert (boost::any_cast<int>(compare_one(5, 6)) == 6);
    assert (boost::any_cast<string>(compare_one(1, string("2,3")))== "2,3");
    assert (boost::any_cast<string>(compare_one(string("5,1"), string("6"))) == "6");
    assert (boost::any_cast<string>(compare_one(string("1"), string("2"))) == "2");
    assert (boost::any_cast<string>(compare_one(string("1"), 1)) == "None");
}

