string file_name_check(const string& file_name) {
    // Check for exactly one dot
    size_t dot_pos = file_name.find('.');
    if (dot_pos == string::npos || file_name.find('.', dot_pos + 1) != string::npos) {
        return "No";
    }

    // Check the substring before the dot
    string name_part = file_name.substr(0, dot_pos);
    if (name_part.empty() || !isalpha(name_part[0])) {
        return "No";
    }

    int digit_count = 0;
    for (char ch : name_part) {
        if (isdigit(ch)) {
            digit_count++;
        }
        if (digit_count > 3) {
            return "No";
        }
    }

    // Check the substring after the dot
    string extension = file_name.substr(dot_pos + 1);
    vector<string> valid_extensions = {"txt", "exe", "dll"};
    if (find(valid_extensions.begin(), valid_extensions.end(), extension) == valid_extensions.end()) {
        return "No";
    }

    return "Yes";
}


