int sum_squares(vector<int> lst) {
    int sum = 0;

    for (size_t i = 0; i < lst.size(); ++i) {
        if (i % 3 == 0) {
            // Square the element if the index is a multiple of 3
            sum += lst[i] * lst[i];
        } else if (i % 4 == 0) {
            // Cube the element if the index is a multiple of 4 and not a multiple of 3
            sum += lst[i] * lst[i] * lst[i];
        } else {
            // Add the element as it is if the index is neither a multiple of 3 nor 4
            sum += lst[i];
        }
    }

    return sum;
}

